INSERT INTO experiences (id, business_id, title, description, short_description, category, difficulty_level, duration_hours, max_participants, min_participants, price_per_person, includes, excludes, meeting_point, what_to_bring, age_restrictions, physical_requirements, weather_dependencies, cancellation_policy, languages, tags, seasonal_availability, special_offers, created_at, updated_at) VALUES
('demo-exp-001', 'demo-business-001', 'Sunset Sailing & Wine Tasting', 'Experience the magic of the French Riviera aboard our luxury sailing yacht. Watch the sun set over the Mediterranean while enjoying premium local wines and gourmet appetizers. Our expert captain will share stories of the coastline and guide you to hidden coves only accessible by boat.', 'Luxury sunset sailing with wine tasting along the French Riviera', 'luxury', 'beginner', 4, 8, 2, 185.00, ARRAY['Professional captain and crew', 'Premium wine selection', 'Gourmet appetizers', 'Snorkeling equipment', 'All safety equipment'], ARRAY['Transportation to marina', 'Gratuities', 'Additional beverages'], 'Port de Cannes, Quai Saint-Pierre', ARRAY['Sunscreen', 'Hat', 'Comfortable clothing', 'Camera'], '{"min_age": 18, "max_age": null}', 'Moderate mobility required for boarding', 'Weather dependent - may be rescheduled', 'Free cancellation 48 hours prior', ARRAY['French', 'English'], ARRAY['sunset', 'wine', 'luxury', 'romantic', 'photography'], '{"available_months": [4,5,6,7,8,9,10], "peak_season": [6,7,8]}', ARRAY[], NOW(), NOW()),
('demo-exp-002', 'demo-business-001', 'Half-Day Coastal Discovery', 'Explore the stunning coastline between Cannes and Antibes on this half-day sailing adventure. Perfect for families and beginners, this trip includes swimming stops, snorkeling opportunities, and a delicious onboard lunch featuring local Mediterranean cuisine.', 'Family-friendly coastal sailing with swimming and lunch', 'family', 'beginner', 5, 10, 4, 125.00, ARRAY['Professional captain', 'Mediterranean lunch', 'Snorkeling gear', 'Swimming stops', 'Safety briefing'], ARRAY['Drinks (available for purchase)', 'Transportation', 'Towels'], 'Port de Cannes, Quai Saint-Pierre', ARRAY['Swimwear', 'Towel', 'Sunscreen', 'Water bottle'], '{"min_age": 6, "max_age": null}', 'Basic swimming ability recommended', 'Operates in most weather conditions', 'Free cancellation 24 hours prior', ARRAY['French', 'English'], ARRAY['family', 'swimming', 'snorkeling', 'lunch', 'coastal'], '{"available_months": [3,4,5,6,7,8,9,10,11], "peak_season": [6,7,8]}', ARRAY['Early bird: 10% off bookings made 30 days in advance'], NOW(), NOW());
