INSERT INTO reviews (id, booking_id, experience_id, user_id, rating, title, comment, experience_highlights, would_recommend, photos, response_from_business, helpful_votes, created_at, updated_at) VALUES
('demo-review-001', 'demo-booking-001', 'demo-exp-001', 'demo-user-001', 5, 'Absolutely Magical Evening!', 'This was hands down the best part of our trip to the French Riviera! Captain Marie was incredibly knowledgeable and made us feel so welcome. The wine selection was excellent and the sunset views were breathtaking. We saw dolphins! Highly recommend SeaFable for anyone looking for a premium sailing experience.', ARRAY['Stunning sunset views', 'Excellent wine selection', 'Professional and friendly captain', 'Dolphin sighting', 'Perfect weather'], true, ARRAY[], 'Thank you Sarah! It was our pleasure to share this magical evening with you. We''re so glad you enjoyed the dolphin encounter - they always make the experience extra special! - Captain Marie', 8, NOW() - INTERVAL '5 days', NOW() - INTERVAL '5 days'),
('demo-review-002', 'demo-booking-002', 'demo-exp-002', 'demo-user-003', 4, 'Great Family Experience', 'Perfect introduction to sailing for our family. The kids loved the swimming stops and the captain was very patient with all their questions. The lunch was delicious and the whole crew made sure everyone felt safe and comfortable. Only minor issue was the wind was quite strong, but that''s nature! Would definitely book with SeaFable again.', ARRAY['Kid-friendly crew', 'Great swimming spots', 'Delicious Mediterranean lunch', 'Safety-focused', 'Educational for children'], true, ARRAY[], 'Thank you for choosing SeaFable for your family adventure! We''re delighted the children enjoyed their first sailing experience. The Mediterranean can be unpredictable, but it sounds like you made the most of it! - The SeaFable Team', 3, NOW() - INTERVAL '2 days', NOW() - INTERVAL '2 days');
